import { ref, computed } from 'vue';
import axios from 'axios';
import FileErrorDisplay from './components/FileErrorDisplay.vue';
import QuizResults from './components/QuizResults.vue';
import FlashcardQuiz from './components/FlashcardQuiz.vue';

// File handling state

export default {
  __name: 'App',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const selectedFiles = ref([]);
    const fileInput = ref(null);
    const unsupportedFiles = ref([]);
    const extractionErrors = ref([]);

    // Quiz state
    const questionCount = ref('');
    const selectedDifficulty = ref('medium');
    const isLoading = ref(false);
    const error = ref('');
    const predictedScore = ref(null);
    const quizStarted = ref(false);
    const quizFinished = ref(false);
    const adaptiveQuestions = ref([]);
    const userResponses = ref([]);

    // Computed properties
    const isFormValid = computed(() => {
      return selectedFiles.value.length > 0 && questionCount.value > 0;
    });
    const hasFileErrors = computed(() => {
      return extractionErrors.value.length > 0 || unsupportedFiles.value.length > 0;
    });
    const getPredictedScoreClass = computed(() => {
      if (predictedScore.value >= 80) return 'high';
      if (predictedScore.value >= 60) return 'medium';
      return 'low';
    });

    // File handling methods
    const triggerFileInput = () => {
      fileInput.value.click();
    };
    const handleFileSelect = event => {
      const files = Array.from(event.target.files);
      addFiles(files);
    };
    const handleDrop = event => {
      const files = Array.from(event.dataTransfer.files);
      addFiles(files);
    };
    const addFiles = files => {
      const validTypes = ['application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document', 'application/vnd.ms-powerpoint', 'application/vnd.openxmlformats-officedocument.presentationml.presentation', 'text/plain', 'application/pdf', 'image/png', 'image/jpeg', 'image/jpg', 'image/tiff', 'image/bmp'];
      const validFiles = [];
      const invalidFiles = [];
      files.forEach(file => {
        if (validTypes.includes(file.type)) {
          validFiles.push(file);
        } else {
          invalidFiles.push(file);
        }
      });
      selectedFiles.value = [...selectedFiles.value, ...validFiles];
      unsupportedFiles.value = [...unsupportedFiles.value, ...invalidFiles];
    };
    const removeFile = index => {
      selectedFiles.value.splice(index, 1);
    };

    // Quiz handling methods
    const handleSubmit = async () => {
      if (!isFormValid.value) return;
      isLoading.value = true;
      error.value = '';
      adaptiveQuestions.value = [];
      predictedScore.value = null;
      quizStarted.value = false;
      userResponses.value = [];
      try {
        const fileContents = await Promise.all(selectedFiles.value.map(file => readFileContent(file)));
        const combinedContent = fileContents.join('\n\n');
        const requestedQuestionCount = Math.min(Number(questionCount.value) * 3, 30);
        const response = await generateQuestions(combinedContent, requestedQuestionCount);
        const processedQuiz = processQuizResponse(response);
        adaptiveQuestions.value = selectQuestions(processedQuiz.questions);
        predictedScore.value = calculatePredictedScore(combinedContent);
      } catch (err) {
        error.value = `Error generating questions: ${err.response?.data?.error?.message || err.message}`;
      } finally {
        isLoading.value = false;
      }
    };
    const startQuiz = () => {
      quizStarted.value = true;
      quizFinished.value = false;
    };
    const handleQuizComplete = responses => {
      userResponses.value = responses;
      quizStarted.value = false;
      quizFinished.value = true;
    };
    const restartQuiz = () => {
      quizStarted.value = true;
      quizFinished.value = false;
      userResponses.value = [];
    };
    const resetToMainScreen = () => {
      selectedFiles.value = [];
      unsupportedFiles.value = [];
      extractionErrors.value = [];
      questionCount.value = '';
      error.value = '';
      isLoading.value = false;
      predictedScore.value = null;
      quizStarted.value = false;
      quizFinished.value = false;
      adaptiveQuestions.value = [];
      userResponses.value = [];
      if (fileInput.value) {
        fileInput.value.value = '';
      }
    };

    // Helper functions
    async function readFileContent(file) {
      return new Promise(resolve => {
        if (file.type === "application/pdf" || file.type === "application/vnd.openxmlformats-officedocument.presentationml.presentation" || file.type === "application/vnd.openxmlformats-officedocument.wordprocessingml.document" || file.type.startsWith('image/')) {
          const formData = new FormData();
          formData.append('file', file);
          const backendUrl = process.env.VUE_APP_BACKEND_URL || 'http://localhost:5001';
          fetch(`${backendUrl}/extract-file`, {
            method: 'POST',
            body: formData
          }).then(res => res.json()).then(data => {
            if (data.text) {
              resolve(data.text);
            } else {
              extractionErrors.value.push({
                fileName: file.name,
                message: data.error || 'Failed to extract text from file'
              });
              resolve("[Could not extract file text]");
            }
          }).catch(() => {
            extractionErrors.value.push({
              fileName: file.name,
              message: 'Server error while extracting text'
            });
            resolve("[Could not extract file text]");
          });
        } else if (file.type === "text/plain") {
          const reader = new FileReader();
          reader.onload = e => resolve(e.target.result);
          reader.onerror = () => {
            extractionErrors.value.push({
              fileName: file.name,
              message: 'Error reading text file'
            });
            resolve("[Could not read file]");
          };
          reader.readAsText(file);
        } else {
          resolve(`[${file.name} is not a supported file type.]`);
        }
      });
    }
    async function generateQuestions(content, count) {
      const prompt = `Create a quiz with ${count} COMPLETELY UNIQUE and DIVERSE questions based on this text. Each question MUST cover a different concept or aspect. NO REPETITIVE or SIMILAR questions allowed.

Format requirements for each question:

For multiple choice questions:
1. Question: [unique question text]
2. Options (mark correct with *):
   A) [option]
   B) [option]
   C) [option]
   D) [option]
3. Explanation: [detailed explanation]
4. Difficulty: [easy|medium|hard]

For true/false questions:
1. True/False: [unique question text]
2. Options (mark correct with *):
   True
   False
3. Explanation: [detailed explanation]
4. Difficulty: [easy|medium|hard]

IMPORTANT:
- Each question MUST cover a different topic/concept
- NO similar or overlapping questions
- Distribute questions evenly across difficulties
- Make questions engaging and thought-provoking
- Ensure clear, unambiguous wording

Text to base questions on:
${content}`;
      const response = await axios.post(`https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${process.env.VUE_APP_GEMINI_API_KEY}`, {
        contents: [{
          parts: [{
            text: prompt
          }]
        }],
        generationConfig: {
          temperature: 0.8,
          topK: 40,
          topP: 0.95,
          maxOutputTokens: 2048
        }
      }, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      return response.data.candidates[0].content.parts[0].text;
    }
    function processQuizResponse(response) {
      const questions = [];
      const lines = response.split('\n');
      let currentQuestion = null;
      for (let i = 0; i < lines.length; i++) {
        const line = lines[i].trim();
        if (!line) continue;
        if (/^\d*\.?\s*(Question:|Q:|Easy Question:|Hard Question:|Medium Question:)/i.test(line)) {
          if (currentQuestion) questions.push(currentQuestion);
          currentQuestion = {
            text: line.replace(/^\d*\.?\s*(Question:|Q:|Easy Question:|Hard Question:|Medium Question:)/i, '').trim(),
            type: 'multiple-choice',
            options: {},
            correctAnswer: '',
            explanation: '',
            difficulty: 'medium'
          };
        } else if (/^\d*\.?\s*(True\/False:|True or False:)/i.test(line)) {
          if (currentQuestion) questions.push(currentQuestion);
          currentQuestion = {
            text: line.replace(/^\d*\.?\s*(True\/False:|True or False:)/i, '').trim(),
            type: 'true-false',
            options: {
              'true': 'True',
              'false': 'False'
            },
            correctAnswer: '',
            explanation: '',
            difficulty: 'medium'
          };
        } else if (currentQuestion) {
          const optionMatch = line.match(/^([A-Da-d])[).]\s*(.*)/);
          if (optionMatch && currentQuestion.type === 'multiple-choice') {
            const letter = optionMatch[1].toUpperCase();
            let option = optionMatch[2].replace(/\*$/, '').trim();
            if (option.includes('*')) {
              currentQuestion.correctAnswer = letter;
              option = option.replace('*', '').trim();
            }
            currentQuestion.options[letter] = option;
          } else if (line.toLowerCase().startsWith('true') || line.toLowerCase().startsWith('false')) {
            const answer = line.toLowerCase().replace(/\*$/, '').trim();
            if (line.includes('*')) {
              currentQuestion.correctAnswer = answer;
            }
          } else if (line.startsWith('Explanation:')) {
            currentQuestion.explanation = line.replace('Explanation:', '').trim();
          } else if (line.startsWith('Difficulty:')) {
            const diff = line.replace('Difficulty:', '').trim().toLowerCase();
            if (['easy', 'medium', 'hard'].includes(diff)) {
              currentQuestion.difficulty = diff;
            }
          }
        }
      }
      if (currentQuestion) questions.push(currentQuestion);
      return {
        questions
      };
    }
    function selectQuestions(questions) {
      const shuffledQuestions = [...questions].sort(() => Math.random() - 0.5).filter((question, index, self) => index === self.findIndex(q => q.text.toLowerCase().replace(/[^\w\s]/g, '') === question.text.toLowerCase().replace(/[^\w\s]/g, '')));
      const selectedQuestions = [];
      const targetPerDifficulty = Math.ceil(Number(questionCount.value) / 3);
      const difficultyCount = {
        easy: 0,
        medium: 0,
        hard: 0
      };
      for (const difficulty of ['easy', 'medium', 'hard']) {
        const questionsOfDifficulty = shuffledQuestions.filter(q => q.difficulty === difficulty && !selectedQuestions.includes(q));
        for (const question of questionsOfDifficulty) {
          if (difficultyCount[difficulty] < targetPerDifficulty && selectedQuestions.length < Number(questionCount.value)) {
            selectedQuestions.push(question);
            difficultyCount[difficulty]++;
          }
        }
      }
      while (selectedQuestions.length < Number(questionCount.value)) {
        const remainingQuestion = shuffledQuestions.find(q => !selectedQuestions.includes(q));
        if (remainingQuestion) {
          selectedQuestions.push(remainingQuestion);
        } else {
          break;
        }
      }
      return selectedQuestions;
    }
    function calculatePredictedScore(content) {
      const contentCoverage = analyzeContentForCoverage();
      const diffValue = getDifficultyLevelValue(selectedDifficulty.value);
      return predictScore(contentCoverage, diffValue);
    }
    function analyzeContentForCoverage() {
      return 100;
    }
    function getDifficultyLevelValue(diff) {
      if (diff === 'easy') return 1;
      if (diff === 'medium') return 2;
      if (diff === 'hard') return 3;
      return 2;
    }
    function predictScore(contentCoverage, difficultyLevel) {
      const a = 0.5;
      const b = -10;
      const c = 90;
      return Math.max(0, Math.min(100, Math.round(a * contentCoverage + b * difficultyLevel + c)));
    }

    // Results formatting
    const formatQuestionsForResults = computed(() => {
      return adaptiveQuestions.value.slice(0, userResponses.value.length).map((q, index) => ({
        text: q.text,
        type: q.type,
        options: q.options,
        answer: q.type === 'multiple-choice' ? `${q.correctAnswer}) ${q.options[q.correctAnswer]}` : q.correctAnswer.charAt(0).toUpperCase() + q.correctAnswer.slice(1),
        explanation: q.explanation,
        userAnswer: userResponses.value[index].userAnswer,
        isCorrect: userResponses.value[index].correct,
        correctAnswer: q.correctAnswer
      }));
    });
    const formatUserAnswersForResults = computed(() => {
      return userResponses.value.map(r => r.userAnswer);
    });
    const correctAnswersCount = computed(() => {
      return userResponses.value.filter(r => r.correct).length;
    });
    const __returned__ = {
      selectedFiles,
      fileInput,
      unsupportedFiles,
      extractionErrors,
      questionCount,
      selectedDifficulty,
      isLoading,
      error,
      predictedScore,
      quizStarted,
      quizFinished,
      adaptiveQuestions,
      userResponses,
      isFormValid,
      hasFileErrors,
      getPredictedScoreClass,
      triggerFileInput,
      handleFileSelect,
      handleDrop,
      addFiles,
      removeFile,
      handleSubmit,
      startQuiz,
      handleQuizComplete,
      restartQuiz,
      resetToMainScreen,
      readFileContent,
      generateQuestions,
      processQuizResponse,
      selectQuestions,
      calculatePredictedScore,
      analyzeContentForCoverage,
      getDifficultyLevelValue,
      predictScore,
      formatQuestionsForResults,
      formatUserAnswersForResults,
      correctAnswersCount,
      ref,
      computed,
      get axios() {
        return axios;
      },
      FileErrorDisplay,
      QuizResults,
      FlashcardQuiz
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
