                const arrayBuffer = e.target.result;
                const doc = new Document(arrayBuffer);
                const text = doc.getParagraphs().map(p => p.text).join('\n\n');
                if (!text.trim()) {
                  throw new Error('No text content found in DOCX');
                }
                resolve(text);
              } catch (error) {
                console.error('Error extracting DOCX text:', error);
                extractionErrors.value.push({
                  fileName: file.name,
                  message: `Error extracting DOCX text: ${error.message}`
                });
                resolve('');
              }
            }
            // Handle PPTX files
            else if (file.type === "application/vnd.openxmlformats-officedocument.presentationml.presentation" || file.name.toLowerCase().endsWith('.pptx')) {
              try {
                const arrayBuffer = e.target.result;
                const parser = new PptxParser();
                const pptx = await parser.parse(arrayBuffer);
                let text = '';
                pptx.slides.forEach(slide => {
                  if (slide.text) {
                    text += slide.text + '\n\n';
                  }
                });
                if (!text.trim()) {
